INSERT INTO vaccin VALUES (1,'Act-Hib',0,NULL,'Ce vaccin est indiqué pour la prévention des infections invasives à Haemophilus influenzae type b (méningites, septicémies, cellulites, arthrites, épiglottites...) chez l enfant à partir de 2 mois.Ce vaccin ne protège pas contre les infections dues à d autres types d Haemophilus influenzae, ni contre les méningites dues à  d autres origines.','VRAI');
INSERT INTO vaccin VALUES (2,'Avaxim 160',192,NULL,'Ce vaccin est indiqué pour l immunisation active contre l infection provoquée par le virus de l hépatite A chez l adolescent à partir de 16 ans et chez l adulte.','VRAI');